{% block title %}Offers!{% endblock %}

{% block body %}
    <table class=\"table\">
        <tr>
            <th>Id</th>
            <th>Image</th>
            <th>{{ knp_pagination_sortable(pagination, 'Name', 'offer.name') }}</th>
            <th>{{ knp_pagination_sortable(pagination, 'Cash Back', 'offer.cashBack') }}
            </th>
        </tr>

        {# table body #}
        {% for item in pagination %}
            <tr {% if loop.index is odd %}class=\"color\"{% endif %}>
                <td>{{ item.id }}</td>
                <td><img src=\"{{ item.imageUrl }}\" /></td>
                <td>{{ item.name }}</td>
                <td>{{ item.cashBack }}</td>
            </tr>
        {% endfor %}
    </table>
    <div class=\"navigation\">
        {{ knp_pagination_render(pagination) }}
    </div>

{% endblock %}
